        static member ERange(left:Pattern, right:String, escape:Char) = PatternBindings.Ranger(left, p right, p escape)
        static member ERange(left:Pattern, right:String, escape:Capture ref) = PatternBindings.Ranger(left, p right, p escape)
        static member ERange(left:Pattern, right:Char, escape:Pattern) = PatternBindings.Ranger(left, p right, escape)
