        static member ERange(left:Pattern, right:Capture ref, escape:Char) = PatternBindings.Ranger(left, p right, p escape)
        static member ERange(left:Pattern, right:Capture ref, escape:Capture ref) = PatternBindings.Ranger(left, p right, p escape)
        static member ERange(left:String, right:Pattern, escape:Pattern) = PatternBindings.Ranger(p left, right, escape)
