        static member ERange(left:String, right:String, escape:Pattern) = PatternBindings.Ranger(p left, p right, escape)
        static member ERange(left:String, right:String, escape:String) = PatternBindings.Ranger(p left, p right, p escape)
        static member ERange(left:String, right:String, escape:Char) = PatternBindings.Ranger(p left, p right, p escape)
        static member ERange(left:String, right:String, escape:Capture ref) = PatternBindings.Ranger(p left, p right, p escape)
        static member ERange(left:String, right:Char, escape:Pattern) = PatternBindings.Ranger(p left, p right, escape)
        static member ERange(left:String, right:Char, escape:String) = PatternBindings.Ranger(p left, p right, p escape)
