        static member ERange(left:Capture ref, right:String, escape:Capture ref) = PatternBindings.Ranger(p left, p right, p escape)
        static member ERange(left:Capture ref, right:Char, escape:Pattern) = PatternBindings.Ranger(p left, p right, escape)
        static member ERange(left:Capture ref, right:Char, escape:String) = PatternBindings.Ranger(p left, p right, p escape)
        static member ERange(left:Capture ref, right:Char, escape:Char) = PatternBindings.Ranger(p left, p right, p escape)
