        static member NRange(left:Pattern, right:Pattern) = PatternBindings.NestedRanger(left, right)
        static member NRange(left:Pattern, right:String) = PatternBindings.NestedRanger(left, p right)
        static member NRange(left:String, right:Pattern) = PatternBindings.NestedRanger(p left, right)
        static member NRange(left:String, right:String) = PatternBindings.NestedRanger(p left, p right)
        static member NRange(left:Pattern, right:Char) = PatternBindings.NestedRanger(left, p right)
