        static member NRange(left:Char, right:String) = PatternBindings.NestedRanger(p left, p right)
        static member NRange(left:String, right:Char) = PatternBindings.NestedRanger(p left, p right)
        static member NRange(left:Capture ref, right:Capture ref) = PatternBindings.NestedRanger(p left, p right)
        static member NRange(left:Pattern, right:Capture ref) = PatternBindings.NestedRanger(left, p right)
        static member NRange(left:Capture ref, right:Pattern) = PatternBindings.NestedRanger(p left, right)
